module.exports = ls

function ls(dir, extNoDot, callback) {
  var fs = require('fs');
  var path = require('path');
  ext = '.' + extNoDot
  fs.readdir(dir, (err, files) => {
    if (err) return callback(err);
    return callback(null, files.filter( file => { return path.extname(file) === ext }));
  });
}
